element.calculation:
(function calculatedFieldValue(current) {

	// Add your code here
	var value = 0;
	var collectionGr = new GlideRecord('x_759224_magic_c_0_mtg_collection');
	collectionGr.addQuery('owner',current.owner);
	collectionGr.query();
	while(collectionGr.next()){
	value = value + +collectionGr.getValue('price');
	}
	return value;  // return the calculated value

})(current);
