(function calculatedFieldValue(current) {
	var retval = '';
	var change = current.total_price - current.pt_price;
	change = change.toFixed(2);
	if(change == 0.00){
		retval = current.calculatedFieldValue;
	}
	retval = change;
    return retval; // return the calculated value
})(current);

