(function calculatedFieldValue(current) {
	var price = 0;
	var cardGr = new GlideRecord('x_759224_magic_c_0_mtg_cards');
	cardGr.addQuery('name', current.getValue('name'));
	cardGr.addQuery('owner', current.getValue('owner'));
	cardGr.query();
	while(cardGr.next()){
		price = price + +cardGr.getValue('total_price');
		}

	return price;  // return the calculated value

})(current);

(function calculatedFieldValue(current) {
	var quantity = 0;
	var cardGr = new GlideRecord('x_759224_magic_c_0_mtg_cards');
	cardGr.addQuery('name', current.getValue('name'));
	cardGr.addQuery('owner', current.getValue('owner'));
	cardGr.query();
	while(cardGr.next()){
		quantity = parseInt(quantity) + parseInt(cardGr.getValue('quantity'));
	}
	return quantity;  // return the calculated value
})(current);

