element.calculation:
(function calculatedFieldValue(current) {

	// Add your code here
	var colors = '';
	var deckCardGR = new GlideRecord('x_759224_magic_c_0_deck_card');
	deckCardGR.addQuery('deck',current.sys_id);
	deckCardGR.addQuery('is_commander',true);
	deckCardGR.query();
	while(deckCardGR.next()){
		var card = deckCardGR.card.getRefRecord();
		if(colors != ''){
			colors += ',' + card.color_identity;
		}else{
		colors += card.color_identity;
		}
	}
	
	
	return colors;  // return the calculated value

})(current);

(function calculatedFieldValue(current) {

	// Add your code here
	var deckCardGR = new GlideRecord('x_759224_magic_c_0_deck_card');
	deckCardGR.addQuery('deck',current.getUniqueValue());
	deckCardGR.addQuery('is_maybeboard','false');
	deckCardGR.query();
	var sum = 0;
	while(deckCardGR.next()){
		sum += deckCardGR.quantity;
	}
	return sum;  // return the calculated value

})(current);

(function calculatedFieldValue(current) {
	var sum = 0;
	var cards = 0;
	var deckCardGR = new GlideRecord('x_759224_magic_c_0_deck_card');
	deckCardGR.addQuery('deck',current.getUniqueValue());
	deckCardGR.addQuery('is_maybeboard','false');
	deckCardGR.query();

	while(deckCardGR.next()){
		sum += (parseInt(deckCardGR.getValue('quantity')) * parseInt(deckCardGR.card.cmc));
		cards += parseInt(deckCardGR.getValue('quantity'));
	}
	gs.addInfoMessage(sum);
	var cmc = sum /cards;
	return cmc.toFixed(2);  // return the calculated value

})(current);
