sp_angular_provider.script:
function() {
    var cardObj;
    var listeners = [];

    function setCard(card) {
        cardObj = card;
        console.log("Card set:", cardObj);

        // Notify all listeners
        listeners.forEach(function(callback) {
            if (typeof callback === 'function') {
                callback(cardObj);
            }
        });
    }

    function getCard() {
        console.log("Getting card:", cardObj);
        return cardObj;
    }

    function onChange(callback) {
        if (typeof callback === 'function') {
            listeners.push(callback);
        }
    }

    return {
        setCard: setCard,
        getCard: getCard,
        onChange: onChange
    };
}
