sp_widget.client_script:
api.controller = function($scope,$location) {
    /* widget controller */
    var c = this;

    $scope.viewDeck = function(deckId) {
        // Navigate to deck_view page with deck_id param
        $location.url('?id=deck_view&deck_id=' + deckId);
    };
};

