sp_widget.script:
(function() {
  var gr = new GlideRecord('x_759224_magic_c_0_mtg_deck');
  gr.query();

  var decks = [];
  while (gr.next()) {
    decks.push({
      name: gr.getValue('name'),
      description: gr.getValue('description'),
      format: gr.getDisplayValue('format'),
      sys_id: gr.getUniqueValue()
    });
  }

  data.decks = decks;
})();
