sp_widget.client_script:
api.controller = function($rootScope) {
  var c = this;
  c.searchTerm = '';

  c.searchCards = function() {
    $rootScope.$broadcast('searchTerm', c.searchTerm);
  };

  c.clearSearch = function() {
    c.searchTerm = '';
    $rootScope.$broadcast('searchTerm', c.searchTerm);
  };
};

