sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;
	c.favorite = false;
// Input:
// Purpose: Allows the user to favorite a card via button press
// Return:
c.favoriteCard = function(){
	c.favorite = !(c.favorite);
	console.log("Favorite: " + c.favorite);
};
// Input:
// Purpose: Opens a modal that displays all of the card details
// Return:
c.viewDetils = function(){
	details = !(details);
	console.log("Details: " + details);
};

};

