sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
		//data.table = $sp.getValue('x_759224_magic_c_0_mtg_cards');

	if(!input){
		//Query the Cards Table
		var cards = new GlideRecord('x_759224_magic_c_0_mtg_cards');
		//If a query is provided apply it
		if(options.query){
			cards.addEncodedQuery(options.query);
		}
		cards.setLimit(options.limit);
		cards.query();
		//Array of cards to use
		data.records = [];

		while(cards.next()){
			var card = {
				name: cards.getValue('name'),
				set: cards.getDisplayValue('sets'),
				quantity: cards.getValue('quantity'),
				price: cards.getValue('total_price'),
				fImg : cards.getValue('front_image'),
				bImg : cards.getValue('back_image'),
				displayImg : true
			};
			data.records.push(card);
		}
	}

})();
