sp_widget.client_script:
api.controller = function(spModal, $rootScope, $scope) {
    /* widget controller */
    var c = this;
    c.cards = [];


    $rootScope.$on('searchTerm', function(event, msg) {
        c.data.searchTerm = msg;
        c.server.update();
        c.server.refresh();
    });

    // Input:
    // Purpose: Opens a modal that displays all of the card details
    // Return:
    c.viewDetails = function(card) {
        var message = "Price: " + card.price + '<br>' +
            "Quantity: " + card.quantity + '<br>' +
            "Foiling: " + card.foil + '<br>' +
            "Oracle: " + card.oracle + '<br>'
        spModal.open({
            title: card.name,
            message: message,
            buttons: [{
                label: '${Close}',
                primary: true
            }]
        })
    }
};

