sp_widget.client_script:
api.controller = function(spModal, $rootScope) {
    var c = this;

    // Listen for search term broadcasts
    $rootScope.$on('searchTerm', function(event, term) {
        c.data.searchTerm = term;
        c.server.update({
            searchTerm: term
        });
    });

    c.viewDetails = function(card) {
        var message = "<style>.oracle-text img {width:16px;height:auto;}</style>" +
              "<div class='oracle-text'>" +
              "Price: " + card.price + "<br>" +
              "Quantity: " + card.quantity + "<br>" +
              "Foiling: " + card.foil + "<br>" +
              "Oracle: <br>" + card.oracle + 
              "</div>";


        spModal.open({
            title: card.name,
            message: message,
            buttons: [{
                label: '${Close}',
                primary: true
            }]
        });
    };
}

