sp_widget.client_script:
api.controller = function($scope, $sce, mtgCards) {
    $scope.isFlipped = false;

    $scope.flipCard = function() {
        $scope.isFlipped = !$scope.isFlipped;
    };

    $scope.getCard = function() {
        const card = mtgCards.getCard();
        if (card) {
            $scope.card = card;

            // Trust the oracle text HTML
            $scope.trustedOracle = $sce.trustAsHtml(card.face_one.oracle_text || '');
			$scope.trustedManaCost = $sce.trustAsHtml(card.face_one.mana_cost || '');
        }
    };

    mtgCards.onChange(function(updatedCard) {
        $scope.$evalAsync(function() {
            $scope.card = updatedCard;

            // Trust the oracle text HTML
            $scope.trustedOracle = $sce.trustAsHtml(updatedCard.face_one.oracle_text || '');
			$scope.trustedManaCost = $sce.trustAsHtml(updatedCard.face_one.mana_cost || '');
        });
    });

    // Initial load (optional)
    $scope.getCard();
};

