sp_widget.client_script:
api.controller = function($scope, mtgCards) {
    console.log("Receiver widget loaded");

    // Simple polling to wait for card to be set
    var waitForCard = setInterval(function() {
        var card = mtgCards.getCard();
        if (card) {
            console.log("Card received in receiver:", card);
            $scope.card = card;
            $scope.$apply(); // trigger Angular digest cycle
            clearInterval(waitForCard);
        }
    }, 100);
};

