sp_widget.client_script:
api.controller = function($scope, cardDataService) {
    // card and onClose are passed as widget input variables
    var c = this;

    $scope.card = cardDataService.getCard();

    $scope.$watch(function() {
        return cardDataService.getCard();
    }, function(newVal) {
        if (newVal) {
            $scope.card = newVal;
        }
    });

    setTimeout(function() {
        var flipBtn = document.getElementById('flipBtn');
        var flipCard = document.getElementById('flipCard');
        if (flipBtn && flipCard) {
            flipBtn.addEventListener('click', function() {
                flipCard.classList.toggle('flipped');
                var isFlipped = flipCard.classList.contains('flipped');

                const manaCostEl = document.getElementById('manaCost');
                if (manaCostEl) {
                    manaCostEl.innerHTML = isFlipped ? "{{ card.backManaCost }}" : "{{ card.frontManaCost }}";
                    manaCostEl.parentElement.style.display = manaCostEl.innerHTML.trim() ? 'inline' : 'none';
                }

                const colorsEl = document.getElementById('colors');
                if (colorsEl) {
                    colorsEl.innerText = isFlipped ? "{{ backColorsFull }}" : "{{ frontColorsFull }}";
                    colorsEl.parentElement.style.display = colorsEl.innerText.trim() ? 'inline' : 'none';
                }

                const typeLineEl = document.getElementById('typeLine');
                if (typeLineEl) {
                    typeLineEl.innerText = isFlipped ? "{{ card.backTypeLine }}" : "{{ card.typeLine }}";
                }

                const oracleEl = document.getElementById('oracle');
                if (oracleEl) {
                    oracleEl.innerHTML = isFlipped ? "{{ card.backOracle }}" : "{{ card.frontOracle }}";
                    oracleEl.parentElement.style.display = oracleEl.innerHTML.trim() ? 'block' : 'none';
                }

                const flavorEl = document.getElementById('flavor');
                if (flavorEl) {
                    const backFlavor = "{{ card.backFlavor }}".trim();
                    const frontFlavor = "{{ card.frontFlavor }}".trim();
                    if (isFlipped && backFlavor && backFlavor !== "-") {
                        flavorEl.innerText = backFlavor;
                        flavorEl.parentElement.style.display = 'block';
                    } else if (!isFlipped && frontFlavor && frontFlavor !== "-") {
                        flavorEl.innerText = frontFlavor;
                        flavorEl.parentElement.style.display = 'block';
                    } else {
                        flavorEl.parentElement.style.display = 'none';
                    }
                }

                const ptEl = document.getElementById('pt');
                if (ptEl) {
                    const backPower = "{{ card.backPower }}";
                    const backToughness = "{{ card.backToughness }}";
                    const frontPower = "{{ card.frontPower }}";
                    const frontToughness = "{{ card.frontToughness }}";
                    if (isFlipped && backPower && backToughness) {
                        ptEl.innerText = backPower + " / " + backToughness;
                        ptEl.parentElement.style.display = 'inline';
                    } else if (!isFlipped && frontPower && frontToughness) {
                        ptEl.innerText = frontPower + " / " + frontToughness;
                        ptEl.parentElement.style.display = 'inline';
                    } else {
                        ptEl.parentElement.style.display = 'none';
                    }
                }

                const nameEl = document.getElementById('cardName');
                if (nameEl) {
                    const backFlavorName = "{{ card.backFlavorName }}";
                    const backName = "{{ card.backName }}";
                    const frontFlavorName = "{{ card.frontFlavorName }}";
                    const frontName = "{{ card.frontName }}";
                    nameEl.innerText = isFlipped ?
                        (backFlavorName.trim() || backName.trim() || "Unknown Card") :
                        (frontFlavorName.trim() || frontName.trim() || "Unknown Card");
                }
            });
        }
    }, 100);
};

