sys_variable_value.value:
(function execute(inputs, outputs) {
var card = inputs.card;
var resp = inputs.response;

var magicUtils = new x_759224_magic_c_0.magic_utils();
var retObj = magicUtils.setCardValues(card, resp);
outputs.status = retObj.status;
outputs.card = retObj.card;

})(inputs, outputs);

