sys_script_include.script:
var spellbook_utils = Class.create();
spellbook_utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    initialize: function() {
        this.constants = new x_759224_magic_c_0.mtg_constants();
    },


    getRequest: function(deckID) {
        return this.generateRequest(deckID);
    },

    generateRequest: function(deckID) {
        try {
            var retObj = {
                success: true,
                message: '',
                request: {
                    main: [],
                    commanders: []
                }
            };
            if (deckID != null && typeof(deckID) === 'string') { // If DeckID exists and is a string
                var deckCardGR = new GlideRecord(this.constants.tables.DECK_CARDS);
                deckCardGR.addQuery('deck', deckID);
                deckCardGR.addQuery('is_sideboard', false);
                deckCardGR.addQuery('is_maybeboard', false);
                deckCardGR.query();

                while (deckCardGR.next()) { // Loop through main deck / commander cards
                    var line = { // Generate the line object
                        card: deckCardGR.card.name.toString(),
                        quantity: parseInt(deckCardGR.quantity)
                    };

                    if (deckCardGR.is_commander) { // If commander, push line to commanders
                        retObj.request.commanders.push(line);
                    } else { // Else push line to main deck
                        retObj.request.main.push(line);
                    }
                }
                retObj.message = retObj.request.commanders.length + ' cards added to commanders. ' +
                    retObj.request.main.length + ' cards added to main deck.';
            } else { // deckID isn't valid
                retObj.success = false;
                retObj.message = 'DeckID ' + deckID + ' is either null or not a string';
            }
        } catch (ex) {
            retObj.success = false;
            retObj.message = ex.message;
        }
        return retObj;
    },
    type: 'spellbook_utils'


});
