sys_script_include.script:
var deck_utils = Class.create();
deck_utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    initialize: function(debug, noDebugMethods) {
        this.debug = false; // Flag to allow debug or not on this script include
        this.noDebugMethods = []; // Array of methods to not log from

        if (debug) {
            this.debug = debug;
        }

        if (noDebugMethods) {
            this.noDebugMethods = noDebugMethods.split(',');
        }
        this.constants = new x_759224_magic_c_0.mtg_constants();
        // Global Table Variables
        this.CARDS = this.constants.tables.CARDS;
        this.HISTORICAL_PRICES = this.constants.tables.HISTORICAL_CARD_PRICES;
        this.SETS = this.constants.tables.SETS;
        this.TOKEN_ASSOCIATION = this.constants.tables.TOKEN_ASSOCIATION;
        this.OWNED_CARDS = this.constants.tables.OWNED_CARDS;
        this.DECK = this.constants.tables.DECK;
        this.DECK_CARDS = this.constants.tables.DECK_CARDS;
        this.DECK_COMBOS = this.constants.tables.DECK_COMBOS;
    },

    /*
     * Description: Calculates the CMC for the provided DeckID
     * Parameters: [string] deckID: The unique value of the deck record to delete associated records for.
     * Returns: [object]: Contains execution information
     *              [boolean] success: Status indicating whether the operation was successful
     *              [string] message: Any messages from the execution
	 * 				[integer] sum: Total CMC of all the main deck cards in the deck
	 * 				[integer] cards: Total number of non-land main deck cards in the deck
	 * 				[float] cmc: Sum / Cards rounded to 2 decimal places
     */

    calculateCMC: function(deckID) {
        try {
            var retObj = {
                success: true,
                message: '',
                sum: 0,
                cards: 0,
                cmc: 0
            };

            // Look up non-land main deck deck card records
            var deckCardGR = new GlideRecordSecure(this.DECK_CARDS);
            deckCardGR.addQuery('deck', deckID);
            deckCardGR.addQuery('is_maybeboard', 'false');
            deckCardGR.addQuery('card.face_one_type_line', 'DOES NOT CONTAIN', 'Land');
            deckCardGR.query();
            // loop through deck card record
            while (deckCardGR.next()) {
                retObj.sum += (parseInt(deckCardGR.getValue('quantity')) * parseInt(deckCardGR.card.cmc));
                retObj.cards += parseInt(deckCardGR.getValue('quantity'));
            }
            // calculate cmc
            retObj.cmc = (retObj.sum / retObj.cards).toFixed(2);
        } catch (ex) {
            retObj.success = false;
            retObj.message = ex.message;
        }

        return retObj;

    },

	getCardCount: function(deckID){
		try{
			var retObj = {
				success: true,
				message: '',
				cards: 0
			};

			var deckCardAG = new GlideAggregate(this.DECK_CARDS);
			//deckCardAG.get('deck',deckID);

		}catch(ex){
			retObj.success = false;
			retObj.message = ex.message;
		}
	},

    /* Description: Takes in a line from the deck input and checks if it's formatted as a txt file or csv
     * Parameters: [string] - deckString: the full stringified input for the deck
     * Returns: [string]: type of file txt, csv, or unknown	
     */

    parseDeck: function(deckString) {
        var lines = deckString.split('\n');
        var results = [];

        if (lines.length === 0) return results;

        var format = this._detectFormat(lines[0]);

        for (var i = 0; i < lines.length; i++) {
            var line = lines[i].trim();
            if (!line) continue;

            var parsed = (format === "txt") ? this._parseTxtLine(line) :
                (format === "csv") ? this._parseCsvLine(line) :
                (format === "moxfield") ? this._parseMoxfieldLine(line) :
                null;

            if (parsed) results.push(parsed);
        }

        return results;
    },

    _stripHtmlTags: function(html) {
        return html.replace(/<[^>]*>/g, '').trim();
    },



    /* Description: Takes in a GlideRecord object then creates a deck record if it's a valid record
     * Parameters: [object] - importGR: Deck import GlideRecord object 
     * Returns: [object] - deckGR: Created deck record GlideRecord object	
     */

    createDeckFromImport: function(importGR) {
        if (!importGR || !importGR.isValidRecord()) {
            gs.warn("DeckImportUtils: Invalid import record");
            return null;
        }

        var deckGR = new GlideRecord(this.DECK);
        deckGR.initialize();
        deckGR.name = importGR.name;
        deckGR.format = importGR.format;
        deckGR.deck_import = importGR.sys_id; // Optional back-reference
        deckGR.insert();

        return deckGR;
    },

    processCards: function(cardArray, deck) {
        for (var i = 0; i < cardArray.length; i++) {
            var card = cardArray[i];
            this._checkExistingCards(card, deck);
        }
    },

    _checkExistingCards: function(card, deck) {
        var foil_value = card.isFoil ? 'foil' : 'none';
        var cardGr = new GlideRecord(this.CARDS);
        cardGr.addQuery('name', card.name);
        cardGr.addQuery('collector_number', card.collector_number);
        cardGr.addQuery('foiling', foil_value);
        cardGr.query();

        // If a card exists in the system create a deck card record associating it to the deck
        if (cardGr.next()) {
            this._createDeckCard(card, cardGr, deck);
        } else {
            // We need to create a cards record in the system first then create the deck card record.
            cardGr = this._createCard(card);
            this._createDeckCard(card, cardGr, deck);
        }
    },

    _createDeckCard: function(import_card, card, deck) {
        var deckCardGr = new GlideRecord(this.DECK_CARDS);
        deckCardGr.initialize();
        deckCardGr.deck = deck.getUniqueValue();
        deckCardGr.card = card.getUniqueValue();
        deckCardGr.quantity = import_card.quantity || 1;
        deckCardGr.is_commander = import_card.isCommander || false;
        deckCardGr.is_sideboard = import_card.isSideboard;
        deckCardGr.is_maybeboard = import_card.isMaybeboard;
        deckCardGr.notes = import_card.tags;
        deckCardGr.insert();
    },

    _createCard: function(card) {
        // Get the set sys_id
        var setGr = new GlideRecord(this.SETS);
        setGr.addQuery('id', card.set);
        setGr.query();

        if (!setGr.next()) {
            gs.error('Set not found for code: ' + card.set);
            return null;
        }
        // convert true / false into foil / none
        var foil_value = card.isFoil ? 'foil' : 'none';
        // create cards record
        var newCardGr = new GlideRecord(this.CARDS);
        newCardGr.initialize();
        newCardGr.name = card.name;
        newCardGr.collector_number = card.collector_number;
        newCardGr.sets = setGr.getUniqueValue();
        newCardGr.foiling = foil_value;
        newCardGr.insert();
        // return the newly created card GlideRecord
        return newCardGr;
    },

    _createOwnedCard: function(card) {
        var ownedCardGr = new GlideRecord();
    },

    /*
     * Description: Takes in a line from the deck input and checks if it's 	 	formatted as a txt file or csv
     * Parameters: [string] line 
     * Returns: [string] type of file; txt, csv, or unknown	
     */

    _detectFormat: function(line) {
        if (/^\d+\s+.+\s+\([^)]+\)\s+\S+/.test(line)) return "moxfield";
        if (/^\d+x\s+.+\([^)]+\)/.test(line)) return "txt";
        if (/^\d+,.+,.+,.+/.test(line)) return "csv";
        return "unknown";
    },


    /*
     * Description: Parses the deck import if it's a txt file
     * Parameters: [string] txt import
     * Returns: [array] returns an array of objects for each line of the input
     */

    _parseTxtLine: function(line) {
        var match = line.match(/^(\d+)x\s+(.*)\s+\(([^)]+)\)\s+([^\s\[]+)(.*)$/);
        if (!match) return null;

        var quantity = parseInt(match[1], 10);
        var name = match[2].trim();
        var set = match[3].trim();
        var collector_number = match[4].trim();
        var remainder = match[5];

        var isFoil = /\*F\*/.test(remainder);
        var isCommander = /\[.*Commander\{.*\}.*\]/i.test(remainder);

        // Extract raw tags from brackets
        var tagMatch = remainder.match(/\[(.*?)\]/);
        var rawTags = tagMatch ? tagMatch[1] : '';

        // Strip any {...} notation inside tags
        var cleanedTags = rawTags.replace(/\{.*?\}/g, '').trim();

        // Determine flags from cleaned tags
        var tagsLower = cleanedTags.toLowerCase();
        var isMaybeboard = tagsLower.includes('maybeboard');
        var isSideboard = tagsLower.includes('sideboard');

        return {
            quantity: quantity,
            name: name,
            set: set,
            collector_number: collector_number,
            isFoil: isFoil,
            isCommander: isCommander,
            isMaybeboard: isMaybeboard,
            isSideboard: isSideboard,
            tags: cleanedTags
        };
    },

    /*
     * Description: Parses the deck import if it's a csv file
     * Parameters: [string] csv import
     * Returns: [array] returns an array of objects for each line of the input
     */

    _parseCsvLine: function(line) {
        var parts = line.split(',');
        if (parts.length < 2) return null;

        return {
            quantity: parseInt(parts[0], 10),
            name: parts[1].trim(),
            set: parts[2]?.trim() || '',
            collector_number: parts[3]?.trim() || '',
            isFoil: false, // CSV doesn't support
            isCommander: false // CSV doesn't support
        };
    },

    _parseMoxfieldLine: function(line) {
        var match = line.match(/^(\d+)\s+(.*?)\s+\(([^)]+)\)\s+(\S+)(.*)$/);
        if (!match) return null;

        var quantity = parseInt(match[1], 10);
        var name = match[2].trim();
        var set = match[3].trim();
        var collector_number = match[4].trim();
        var remainder = match[5];

        var isFoil = /\*F\*/i.test(remainder);
        var isEtched = /\*E\*/i.test(remainder); // Optional: for foiling field logic
        var isCommander = /\[.*Commander\{.*\}.*\]/i.test(remainder);

        var tagMatch = remainder.match(/\[(.*?)\]/);
        var rawTags = tagMatch ? tagMatch[1] : '';
        var cleanedTags = rawTags.replace(/\{.*?\}/g, '').trim();
        var tagsLower = cleanedTags.toLowerCase();
        var isMaybeboard = tagsLower.includes('maybeboard');
        var isSideboard = tagsLower.includes('sideboard');

        return {
            quantity: quantity,
            name: name,
            set: set,
            collector_number: collector_number,
            isFoil: isFoil,
            isEtched: isEtched,
            isCommander: isCommander,
            isMaybeboard: isMaybeboard,
            isSideboard: isSideboard,
            tags: cleanedTags
        };
    },

    /*
     * Description: Deletes associated records for a given deck ID, including deck cards and combos.
     * Parameters: [string] deckID: The unique value of the deck record to delete associated records for.
     * Returns: [object]: Contains execution information
     *              [boolean] success: Status indicating whether the operation was successful
     *              [string] message: Any messages from the execution
     */

    deleteAssociatedRecords: function(deckID) {
        try {
            var retObj = {
                success: true,
                message: ''
            };

            var cardStatus = this._deleteDeckCards(deckID);
            if (cardStatus && !cardStatus.success) { // If success is false
                retObj.success = false; // update success to false
                retObj.message = cardStatus.message; // set message to the fail message
                return retObj; // stop execution and return
            } else if (cardStatus && cardStatus.success) { // Else if success is true
                retObj.message = cardStatus.message; // Pass success message through
            }
            var comboStatus = this._deleteCombos(deckID);
            if (comboStatus && !comboStatus.success) { // If success is false
                retObj.sucess = false; // update success to false
                retObj.message += "\n" + comboStatus.message; // Add this failure message to message
                return retObj; // stop execution and return
            } else if (comboStatus && comboStatus.success) { // Else if success is true
                retObj.message += "\n" + comboStatus.message; // Pass success message through
            }
            return retObj; // return retObj

        } catch (ex) {
            retObj.success = false;
            retObj.message = ex.message;
        }

    },

    /*
     * Description: Deletes all records associated with a given deck ID from the DECK_CARDS table.
     * Parameters: [string] deckID: The unique value of the deck record to delete associated records for.
     * Returns: [object]: Contains execution information
     *              [boolean] success: Status indicating whether the operation was successful
     *              [string] message: Any messages from the execution
     */

    _deleteDeckCards: function(deckID) {
        try {
            var retObj = {
                success: true,
                message: '',
            };

            var deckCardGR = new GlideRecord(this.DECK_CARDS);
            deckCardGR.get('deck', deckID);
            if (deckCardGR.getRowCount() > 0) {
                deckCardGR.deleteMultiple();
                retObj.message = deckCardGR.getRowCount() + ' deck card records deleted.';
            } else {
                retObj.message = '0 deck card records found.';
            }
        } catch (ex) {
            retObj.success = false;
            retObj.message = ex.message;
        }
        return retObj;
    },

    /*
     * Description: Deletes all records associated with a given deck ID from the DECK_COMBOS table.
     * Parameters: [string] deckID: The unique value of the deck record to delete associated records for.
     * Returns: [object]: Contains execution information
     *              [boolean] success: Status indicating whether the operation was successful
     *              [string] message: Any messages from the execution
     */

    _deleteCombos: function(deckID) {
        try {
            var retObj = {
                success: true,
                message: '',
            };
            var comboGR = new GlideRecord(this.DECK_COMBOS);
            comboGR.get('deck', deckID);
            if (comboGR.getRowCount() > 0) { // If a combo was found
                comboGR.deleteMultiple();
                retObj.message = comboGR.getRowCount() + ' combo records deleted.';
            } else { // Else no combos were found
                retObj.message = '0 combo records found.';
            }
        } catch (ex) {
            retObj.success = false;
            retObj.message = ex.message;
        }
        return retObj;
    },

    type: 'deck_utils'
});
