sys_script_include.script:
var mtg_constants = Class.create();
mtg_constants.prototype = {
    initialize: function() {
        this.PREFIX = 'x_759224_magic_c_0';
        this.tables = {
            // CORE TABLES // 
            CARDS: this.PREFIX + '_card',
            COLORS: this.PREFIX + '_colors',
            SETS: this.PREFIX + '_mtg_sets',
            KEYWORDS: this.PREFIX + '_mtg_keywords',
            USERS: this.PREFIX + '_magic_users',
            TOKEN_ASSOCIATION: this.PREFIX + '_token_association',
            // DECK TABLES //
            DECK: this.PREFIX + '_mtg_deck',
            DECK_CARDS: this.PREFIX + '_deck_card',
            DECK_FORMATS: this.PREFIX + '_deck_formats',
            DECK_IMPORTS: this.PREFIX + '_deck_imports',
            // CARD TABLES //
            HISTORICAL_CARD_PRICES: this.PREFIX + '_historical_card_prices',
            OWNED_CARDS: this.PREFIX + '_owned_card',
            // API TABLES //
            SCRYFALL_QUEUE: this.PREFIX + '_scryfall_api_queue',
        };
        this.api = {
            // Scryfall Base //
            SCRYFALL_BASE_URL: 'https://api.scryfall.com/',
            // Spellbook Base //
            SPELLBOOK_BASE_URL: 'https://backend.commanderspellbook.com/'
        };
        this.images = {
            // Images //
            FOIL_OVERLAY: this.PREFIX + '.foil.png',
            icons: { // Icons used across the platform
                TCGPLAYER: this.PREFIX + '.icon_tcgplayer.png',
                CARDMARKET: this.PREFIX + '.icon_cardmarket.png',
                CARDHOARDER: this.PREFIX + '.icon_cardhoarder.png',
                EDHREC: this.PREFIX + '.icon_edhrec.png'
            }
        };

        // Colors object to hold sys_ids of color choices
        this.colors = {
            W: '',
            U: '',
            B: '',
            R: '',
            G: '',
            C: ''
        };

        // Set Colors Object
        var colorGR = new GlideRecord(this.tables.COLORS);
        colorGR.addQuery('active', true);
        colorGR.query();

        while (colorGR.next()) {
            var colorKey = colorGR.getValue('value');
            this.colors[colorKey] = colorGR.getUniqueValue();
        }
    },

    type: 'mtg_contstants'
};
